import React from "react";
import CarsAndTrafficContext from "./CarsAndTrafficConcext";

class CarsAndTrafficProvider extends React.Component {
  constructor() {
    super();
    this.state = {
      cars: {
        red: false,
        blue: false,
        yellow: false,
      },
      signal: { color: 'red' },
    }
  }

  moveCar = (car) => {
    this.setState((prev) => ({
      cars: {
        [car]: !prev.cars[car],
  }}))
  }
  render() {
    const { Provider } = CarsAndTrafficContext;
    const { children } = this.props;
    return (
      <Provider value= {{...this.state.cars, moveCar: this.moveCar}}>
        {children}
      </Provider>
    );
  }
}

export default CarsAndTrafficProvider;
